# -- Faça um programa em Python que leia uma matriz 3 x 3. O programa deverá preencher com 1 a diagonal principal e ----
# -- com 0 os demais elementos. Escreva ao final a matriz original e a matriz obtida após o processamento. -------------
# ----------------------------------------------------------------------------------------------------------------------
# -- RESOLUÇÃO ---------------------------------------------------------------------------------------------------------
# ----------------------------------------------------------------------------------------------------------------------
cont = 0 # ----------------------------------------------------------------- | Inicia a contagem | ---------------------
numLinhas = 3 # ------------------------------------------------------------ | Numero de linha   | ---------------------
numColunas = 3 # ----------------------------------------------------------- | Numero de Colunas | ---------------------
matriz = [] # -------------------------------------------------------------- | Inicia uma Matriz | ---------------------
# ----------------------------------------------------------------------------------------------------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de numLinhas | ------
    linha = [] # ----------------------------------------------------------- | Inicia linha | --------------------------
    for j in range(numColunas): # ------------------------------------------ | Para j no intervalo de numColunas | -----
        numero = float(input("[LINHA: %i | COLUNA: %i] = " % (i, j))) # ---- | Recebe dos dados | ----------------------
        linha.append(numero) # --------------------------------------------- | Anexa na linha | ------------------------
    matriz.append(linha) # ------------------------------------------------- | Anexa na Matriz | -----------------------
# ----------------------------------------------------------------------------------------------------------------------
print("\n") # -------------------------------------------------------------- | Pula uma Linha | ------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de numLinhas | ------
    print(matriz[i]) # ----------------------------------------------------- | Mostre a Matriz | -----------------------
# ----------------------------------------------------------------------------------------------------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de numLinhas | ------
    for j in range(numColunas): # ------------------------------------------ | Para j no intervalo de numColunas | -----
        if i == j: # ------------------------------------------------------- | Se i for igual a j(0,0, 1,1, 2,2) | -----
            matriz[i][j] = 1 # --------------------------------------------- | Subsititui o valor para 1 | -------------
        else: # ------------------------------------------------------------ | Senão | ---------------------------------
            matriz[i][j] = 0 # --------------------------------------------- | Substitui o valor para 0 | --------------
# ----------------------------------------------------------------------------------------------------------------------
print("\n") # -------------------------------------------------------------- | Pula uma Linha | ------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de numLinhas | ------
    print(matriz[i]) # ----------------------------------------------------- | Mostre a Matriz | -----------------------
# ----------------------------------------------------------------------------------------------------------------------