# -- Faça um programa em Python que leia uma matriz 4 x 4, -------------------------------------------------------------
# -- imprima a matriz e retorne a localização (linha e a coluna) -------------------------------------------------------
# -- do maior valor desta matriz. --------------------------------------------------------------------------------------
import random # --------------------------------------------------------------------------------------------------------
# ----------------------------------------------------------------------------------------------------------------------
numLinhas = 4 # ------------------------------------------------------------ | Definindo o numero de linhas | ----------
numColunas = 4 # ----------------------------------------------------------- | Definindo o numero de colunas | ---------
matriz = [] # -------------------------------------------------------------- | Cria uma matriz vazia | -----------------
# ----------------------------------------------------------------------------------------------------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de linhas | ---------
    linha = [] # ----------------------------------------------------------- | Cria uma linha vazia | ------------------
    for j in range(numColunas): # ------------------------------------------ | Para j no intervalo de colunas | --------
        numero = float(input("LINHA: %i | COLUNA: %i ] = " %(i,j))) # ------ | Recebe os dados | -----------------------
        linha.append(numero) # --------------------------------------------- | Preenche a linha | ----------------------
    matriz.append(linha) # ------------------------------------------------- | Preenche a coluna | ---------------------
print("\n") # -------------------------------------------------------------- | Pula uma Linha | ------------------------
for i in range(numLinhas): # ----------------------------------------------- | Para i no intervalo de linhas | ---------
    print(matriz[i]) # ----------------------------------------------------- | Mostre a Matriz | -----------------------
# ----------------------------------------------------------------------------------------------------------------------
maior_valor = 0 # ---------------------------------------------------------- | Variavel de inicio | --------------------
for i in matriz: # --------------------------------------------------------- | Para i no intervalo da matriz | ---------
    for j in i: # ---------------------------------------------------------- | para j em i | ---------------------------
        if maior_valor < j: # ---------------------------------------------- | se o maior valor for menor que j | ------
            maior_valor = j # ---------------------------------------------- | o Maior valor  é igual a j | ------------
# ----------------------------------------------------------------------------------------------------------------------
maior_valor = -1 # --------------------------------------------------------- | Variavel de inicio | --------------------
for l, i in enumerate(matriz): # ------------------------------------------- | Para linha i enumerada na matriz | ------
    for c, j in enumerate(i): # -------------------------------------------- | Para Coluna j enumerada na matriz | -----
        if maior_valor < j: # ---------------------------------------------- | Se o maior valor for menor que j | ------
            maior_valor = j # ---------------------------------------------- | o Maior valor é igua a j | --------------
            posicao_maior = (l, c) # --------------------------------------- | Posição do maior é = a linha e coluna| --
# ----------------------------------------------------------------------------------------------------------------------
print("\n") # -------------------------------------------------------------- | Pula uma Linha | ------------------------
print("Maior valor: %i" %maior_valor) # ------------------------------------ | Mostra o maior valor| -------------------
print("Posição: [LINHA: %i | COLUNA: %i]" %posicao_maior) # ---------------- | Mostra a posição| -----------------------
# ----------------------------------------------------------------------------------------------------------------------